        public static List<Author> Authors { get; set; } = new()
            {

                new Author { Id = 1, FirstName = "Lev", LastName = "Tolstoy", DateOfBirth = new DateTime(1828, 9, 9), Details = "Lev Tolstoy, Rus yazar, filozof ve toplumsal eleştirmen olarak tanınır. En ünlü eserleri arasında *Savaş ve Barış* ve *Anna Karenina* yer alır. Tolstoy'un eserleri, derin insanlık tasvirleri ve toplumsal eleştirileriyle dünya edebiyatının en önemli örnekleri arasında kabul edilir. Aynı zamanda, dini ve ahlaki düşünceleriyle de etkili olmuştur." },
                new Author { Id = 2, FirstName = "Gustave", LastName = "Flaubert", DateOfBirth = new DateTime(1821, 12, 12), Details = "Gustave Flaubert, Fransız roman yazarı, edebi realizmin öncülerindendir. En ünlü eseri olan *Madame Bovary*, sıradan bir kadının hayal kırıklıklarıyla olan mücadelesini ve bunların toplumsal yapıyı nasıl sorguladığını konu alır. Flaubert'in yazım tarzı detaylara verdiği önem ve dilin estetik kullanımındaki hassasiyetle tanınır." },
                new Author { Id = 3, FirstName = "Fyodor", LastName = "Dostoevsky", DateOfBirth = new DateTime(1821, 11, 11), Details = "Fyodor Dostoyevsky, Rus edebiyatının en önemli figürlerinden biridir. Eserlerinde insan psikolojisini, dini inançları ve ahlaki meseleleri derinlemesine işler. *Suç ve Ceza*, *Karamazov Kardeşler* ve *Beyaz Geceler* gibi eserleriyle tanınır. Dostoyevsky'nin romanları, insanın içsel çatışmalarını ve toplumsal yapıyı sorgulayan derinlikli bir anlatıma sahiptir." },
                new Author { Id = 4, FirstName = "Miguel", LastName = "Cervantes", DateOfBirth = new DateTime(1547, 9, 29), Details = "Miguel de Cervantes, İspanyol yazar, şair ve oyun yazarıdır. *Don Quijote* adlı eseri, modern edebiyatın ilk romanı olarak kabul edilir ve Cervantes'i dünya edebiyatının başyapıtlarından birinin yaratıcısı yapmıştır. Cervantes'in eserleri, zamanının toplumsal ve kültürel eleştirisini içerir, özellikle idealler ile gerçeklik arasındaki çatışmayı işler." },
                new Author { Id = 5, FirstName = "Alexandre", LastName = "Dumas", DateOfBirth = new DateTime(1802, 7, 24), Details = "Alexandre Dumas, Fransız yazar, özellikle tarihi romanlarıyla tanınır. *Üç Silahşörler* ve *Monte Kristo Kontu* gibi eserleri, macera ve kahramanlık temasını işleyerek dünya çapında popülerlik kazanmıştır. Dumas'ın eserleri, aksiyon dolu hikayeler ve karakter derinliği ile tanınır, aynı zamanda Fransız toplumunun farklı sınıflarına dair keskin gözlemler içerir." },
                new Author { Id = 6, FirstName = "Charlotte", LastName = "Brontë", DateOfBirth = new DateTime(1816, 4, 21), Details = "Charlotte Brontë, İngiliz yazar ve şairdir. En bilinen eseri *Jane Eyre*, feminist bir bakış açısıyla yazılmış ve dönemin kadınlarının yaşadığı toplumsal zorlukları anlatan önemli bir romandır. Brontë'nin yazıları, genellikle tutkulu karakterler, içsel çatışmalar ve toplumsal normlara karşı bireysel direniş temalarını işler." },
                new Author { Id = 7, FirstName = "Jane", LastName = "Austen", DateOfBirth = new DateTime(1775, 12, 16), Details = "Jane Austen, İngiliz roman yazarıdır ve özellikle sosyal sınıflar, evlilik ve kadın hakları gibi konuları irdeleyen eserleriyle tanınır. *Gurur ve Önyargı* en ünlü romanıdır ve Austen'in toplumsal sınıf eleştirilerini, karakter analizlerini ve mizahını ortaya koyar. Austen'in eserleri, dönemin kadınlarına olan bakış açısını değiştirmiştir." },
                new Author { Id = 8, FirstName = "Victor", LastName = "Hugo", DateOfBirth = new DateTime(1802, 2, 26), Details = "Victor Hugo, Fransız şair, yazar ve politikacıdır. Eserleri, toplumsal eşitsizlikleri, özgürlüğü ve insan haklarını savunur. *Sefiller* ve *Notre-Dame’ın Kamburu* gibi romanları, Fransız toplumunun farklı katmanlarını anlatan derinlikli ve dramatik hikayelerdir. Hugo'nun çalışmaları, Fransız Devrimi'nin ideallerini ve sonrası toplumsal değişimleri derinlemesine işler." },
                new Author { Id = 9, FirstName = "Jules", LastName = "Verne", DateOfBirth = new DateTime(1828, 2, 8), Details = "Jules Verne, Fransız yazar, bilimkurgu türünün öncüsüdür. *80 Günde Dünya Turu*, *Denizler Altında 20.000 Fersah* gibi eserleriyle, fantastik ve bilimsel keşifleri hikayelerinde birleştirir. Verne, geleceğin teknolojilerini ve insanın keşfetme arzusunu tasvir etmiştir, bilimkurgu türünün temellerini atmıştır." },
                new Author { Id = 10, FirstName = "Bram", LastName = "Stoker", DateOfBirth = new DateTime(1847, 11, 8), Details = "Bram Stoker, İrlandalı yazardır ve en ünlü eseri *Dracula* ile tanınır. *Dracula*, vampir mitolojisini popülerleştirerek korku edebiyatının en önemli eserlerinden biri haline gelmiştir. Stoker, gotik edebiyatın en etkili temsilcilerindendir ve eserlerinde korku, doğaüstü unsurlar ve insanın karanlık yönlerini işler." },
                new Author { Id = 11, FirstName = "Oscar", LastName = "Wilde", DateOfBirth = new DateTime(1854, 10, 16), Details = "Oscar Wilde, İrlandalı şair, oyun yazarı ve romancıdır. Eserleri, mizahi ve eleştirel bir bakış açısı sunarak toplumsal normları sorgular. *Dorian Gray’in Portresi* ve *İyi Arkadaş* gibi eserleri, Wilde'ın zekice diyalogları, keskin toplumsal eleştirileri ve estetik anlayışını yansıtır. Wilde, Viktorya dönemi toplumunun ikiyüzlülüğüne karşı sert eleştirilerde bulunmuştur." },
                new Author { Id = 12, FirstName = "Charles", LastName = "Dickens", DateOfBirth = new DateTime(1812, 2, 7), Details = "Charles Dickens, İngiliz yazar ve toplumsal eleştirmen olarak tanınır. Eserlerinde, işçi sınıfının zorluklarını, çocuk işçiliği gibi sosyal sorunları ve dönemin adalet sistemindeki eşitsizlikleri işler. *Oliver Twist*, *David Copperfield* ve *Büyük Beklentiler* gibi eserleri, Dickens'ın toplumsal eleştirilerinin önemli örnekleridir." },
                new Author { Id = 13, FirstName = "Jack", LastName = "London", DateOfBirth = new DateTime(1876, 1, 12), Details = "Jack London, Amerikalı yazar ve maceracı olarak tanınır. *Beyaz Diş* ve *Vahşi Doğa* gibi eserlerinde, doğa ile insanın mücadelesini ve hayatta kalma temasını işler. London, aynı zamanda işçi hakları ve toplumsal eşitsizlikler gibi konularda" },

                new Author {Id = 14, FirstName = "Harper", LastName = "Lee", DateOfBirth = new DateTime(1926, 4, 28),Details = "Harper Lee, Amerikalı yazar ve Pulitzer ödüllü bir edebiyatçıdır. *Bülbülü Öldürmek* adlı eseri, Amerikan Güneyi'nde ırkçılık ve adalet konularını derinlemesine ele alan bir başyapıttır. Lee, sade ve etkileyici anlatımıyla toplumsal eşitsizliklere dikkat çeker." },

                new Author {Id = 15, FirstName = "George",LastName = "Orwell",DateOfBirth = new DateTime(1903, 6, 25),Details = "George Orwell, İngiliz yazar ve gazeteci olarak tanınır. *1984* ve *Hayvan Çiftliği* gibi eserlerinde, totaliter rejimlerin birey üzerindeki etkilerini ve güç ilişkilerini ele alır. Orwell'in çalışmaları, politik hiciv ve toplumsal eleştiri açısından edebiyat tarihinin en etkili örneklerindendir." },

                new Author {Id = 16,FirstName = "Daniel",LastName = "Defoe",DateOfBirth = new DateTime(1660, 1, 1),Details = "Daniel Defoe, İngiliz yazar ve gazeteci olarak modern romanın öncülerinden biri olarak kabul edilir. *Robinson Crusoe*, hayatta kalma, yalnızlık ve insan doğasına dair derin bir anlatı sunar. Defoe'nun eserleri, macera ve gerçekçiliğin birleşimiyle dikkat çeker."},


